import React, { useState } from "react";

// CalendarProps 인터페이스 정의
interface CalendarProps {
    selectedDay: Date | null;
    setSelectedDay: (day: Date | null) => void;
    isPrevMonth: boolean;
    isNextMonth: boolean;
}

// Calendar 컴포넌트 정의
const Calendar = ({
    selectedDay,
    setSelectedDay,
    isPrevMonth,
    isNextMonth,
}: CalendarProps) => {
    const daysOfWeek = ["일", "월", "화", "수", "목", "금", "토"];
    const [currentMonth, setCurrentMonth] = useState(new Date());

    const today = new Date();
    today.setHours(0, 0, 0, 0);

    // 기타 Calendar 컴포넌트 로직...

    const calendarDays = buildCalendarDays();
    const calendarTags = buildCalendarTag(calendarDays);
    const calendarRows = divideWeek(calendarTags);

    return (
        <div className="calendar">
            <div className="calendarNav">{/* 캘린더 네비게이션 로직... */}</div>
            <table>
                <thead>{/* 테이블 헤드 로직... */}</thead>
                <tbody>
                    {calendarRows.map((row, i) => (
                        <tr key={i}>{row}</tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

// ApplicationForm 컴포넌트 정의 및 export
export default function ApplicationForm() {
    const [selectedDay, setSelectedDay] = (useState < Date) | (null > null);
    const isPrevMonth = true; // 이 값은 예시이며, 실제 로직에 따라 변경될 수 있습니다.
    const isNextMonth = true; // 이 값도 예시입니다.

    return (
        <div className="page-container" style={{ fontSize: "32px" }}>
            기부신청서 화면입니다.
            <Calendar
                selectedDay={selectedDay}
                setSelectedDay={setSelectedDay}
                isPrevMonth={isPrevMonth}
                isNextMonth={isNextMonth}
            />
        </div>
    );
}
