import React, { useState } from "react";
import styled from "styled-components";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

// Styled component 생성
const StyledCalendar = styled(Calendar)`
    &.react-calendar {
        // react-calendar의 기본 클래스명에 대한 스타일
        border-color: #0f4b43;
        background-color: #f5f1ed; // 배경색 변경
        width: 70%;
        hight: 200%;
        position: absolute;
        left: 15%;
        top: 30%;
    }

    .react-calendar__navigation {
        // 네비게이션 버튼 스타일
        background-color: #f5f1ed;
        button {
            min-width: 44px; // 버튼 최소 너비 설정
            background-color: #f5f1ed; // 버튼 배경색 변경
        }
    }

    .react-calendar__month-view__days__day--weekend {
        // 주말 날짜 스타일
        color: #d10000; // 주말 날짜 색상 변경
    }

    .react-calendar__tile:enabled:hover,
    .react-calendar__tile:enabled:focus {
        // 타일 호버 및 포커스 스타일
        background-color: #d7e0f0; // 배경색 변경
        border-radius: 5px; // 테두리 둥글게
    }
`;

function CustomCalendar(props) {
    // 이름을 CustomCalendar로 변경하여 충돌 방지
    const [dated, setDated] = useState(new Date());
    const onChange = (date) => setDated(date);

    return (
        <StyledCalendar
            className="calendar"
            onChange={onChange}
            value={dated}
        />
    );
}

function ApplicationForm(props) {
    return (
        <div id="calendar-box">
            <CustomCalendar />
        </div>
    );
}

export default ApplicationForm; // 컴포넌트 정의 후 export
